curl -H "Content-Type: application/json" --request POST --data '{"user_id": 10, "title": "Tea, Earl Grey, Hot", "difficulty_id": "1", "duration": "7", "image_url": "https://res.cloudinary.com/bitesizerecipes/image/upload/v1618591937/Bite-Size-Images/gbcbtugltshiv1slgz7m.webp", "servings": 1, "description": "Hot, but not too hot.", "unitOfMeasure": 1, "quantity": 1, "instructionSteps": "boil the water\nput the tea bag in the mug\nadd the boiling water\nremove teabag after steeping has completed\nEnjoy!", "ingredientList": "1 mug\n1 tea bag\n1 cup of cold water", "tags": [{"id":10}] }' http://localhost:5001/recipes
curl -H "Content-Type: application/json" --request POST --data '{"user_id": 10, "title": "Kenjis Chicken Stew", "difficulty_id": "2", "duration": "35", "image_url": "https://res.cloudinary.com/bitesizerecipes/image/upload/v1618862561/Bite-Size-Images/st2z1xewhiqzlersdx4x.jpg", "servings": 3, "description": "This dish uses just five ingredients, takes about five minutes of actual work.", "unitOfMeasure": 1, "quantity": 1, "instructionSteps": "Combine potatoes, onion, tomato, chicken pieces, bay leaves, and a large pinch of salt in a pressure cooker.\nToss to combine.\nSeal lid and cook under high pressure for 15 minutes.\nRelease pressure, remove lid, season to taste, and serve\nEnjoy!", "ingredientList": "4 chicken legs\n2 Yukon Gold potatoes\n1 large onion\n1 large tomato\n2 bay leaves\nKosher salt\nfreshly ground black pepper", "tags": [{"id":3}] }' http://localhost:5001/recipes
curl -H "Content-Type: application/json" --request POST --data '{"user_id": 10, "title": "Instant Pot Potato Soup", "difficulty_id": "2", "duration": "40", "image_url": "https://res.cloudinary.com/bitesizerecipes/image/upload/v1618865563/Bite-Size-Images/lwxopqhdjwfynac5rxgu.png", "servings": 4, "description": "This soup is what an Instant Pot was made for. No need to boil potatoes for a long time or stand over a stove.", "unitOfMeasure": 1, "quantity": 1, "instructionSteps": "Set Instant Pot to Sauté function and melt butter. Add onion and cook until soft, 5 minutes, then add garlic and thyme and cook until fragrant, 1 minute more.\nAdd potatoes and broth and place lid on Instant Pot. Set to Pressure Cook on High and set time for 8 minutes.\nIn a small bowl whisk together 2 tablespoons milk and cornstarch to make a slurry.\nRemove lid from Instant Pot and set to Sauté function again. Pour in slurry, remaining 1 cup milk, and let boil about 5 minutes, stirring constantly.\nSeason with salt and pepper to taste.\nEnjoy!", "ingredientList": "2 tbsp of butter\n1 large onion\n2 cloves garlic\n6 large russet potatoes\n4 cups of chicken\n2 tbsp. cornstarch", "tags": [{"id":3}] }' http://localhost:5001/recipes

